// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @author Georgios Konstantopoulos
 * @dev Extension of {ERC20} that allows token holders to use their tokens
 * without sending any transactions by setting {IERC20-allowance} with a
 * signature using the {permit} method, and then spend them via
 * {IERC20-transferFrom}.
 *
 * The {permit} signature mechanism conforms to the {IERC2612} interface.
 */
abstract contract ERC20Permit is ERC20 {
    mapping (address => uint256) public nonces;

	bytes32 public constant PERMIT_TYPEHASH = keccak256("Permit(address holder,address spender,uint256 nonce,uint256 expiry,uint256 amount)");
    // bytes32 public constant PERMIT_TYPEHASH = 0xea2aa0a1be11a07ed86d755c93467f4f82362b452371d1ba94d1715123511acb;
    bytes32 public immutable DOMAIN_SEPARATOR;

    constructor(string memory name_, string memory symbol_) ERC20(name_, symbol_) {
        uint256 chainId;
        assembly {
            chainId := chainid()
        }

        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
                keccak256(bytes(name_)),
                keccak256(bytes(version())),
                chainId,
                address(this)
            )
        );
    }

    /// @dev Setting the version as a function so that it can be overriden
    function version() public pure virtual returns(string memory) { return "1"; }

    /**
     * @dev See {IERC2612-permit}.
     *
     * In cases where the free option is not a concern, deadline can simply be
     * set to uint(-1), so it should be seen as an optional parameter
     */
    // function permit(address owner, address spender, uint256 amount, uint256 deadline, uint8 v, bytes32 r, bytes32 s) public virtual override {
    //     require(deadline >= block.timestamp, "ERC20Permit: expired deadline");

    //     bytes32 hashStruct = keccak256(
    //         abi.encode(
    //             PERMIT_TYPEHASH,
    //             owner,
    //             spender,
    //             amount,
    //             nonces[owner]++,
    //             deadline
    //         )
    //     );

    //     bytes32 hash = keccak256(
    //         abi.encodePacked(
    //             '\x19\x01',
    //             DOMAIN_SEPARATOR,
    //             hashStruct
    //         )
    //     );

    //     address signer = ecrecover(hash, v, r, s);
    //     require(
    //         signer != address(0) && signer == owner,
    //         "ERC20Permit: invalid signature"
    //     );

    //     _approve(owner, spender, amount);
    // }

	 // --- Approve by signature ---
    function permit(
		address holder, 
		address spender, 
		uint256 nonce, 
		uint256 expiry,
        uint256 amount, 
		uint8 v, 
		bytes32 r, 
		bytes32 s
	) 
		external
    {
        bytes32 digest = keccak256(abi.encodePacked(
			"\x19\x01",
			DOMAIN_SEPARATOR,
			keccak256(
				abi.encode(
					PERMIT_TYPEHASH,
					holder,
					spender,
					nonce,
					expiry,
					amount
				)
			)
        ));

        require(holder != address(0), "invalid-address-0");
        require(holder == ecrecover(digest, v, r, s), "invalid-permit");
        require(expiry == 0 || block.timestamp <= expiry, "permit-expired");
        require(nonce == nonces[holder]++, "invalid-nonce");

		_approve(holder, spender, amount);
    }
}

